// Better slug function that handles Mongolian characters
const createSlug = (title) => {
  return title
    .toLowerCase()
    .replace(/\s+/g, '-') // Replace spaces with hyphens
    .replace(/[^\u0400-\u04FF\w-]/g, '') // Keep Cyrillic, Latin, numbers, and hyphens
    .trim();
};

